var searchButton = document.querySelector(".search-1"),searchForm = document.querySelector(".search-body"),arrivalDate = searchForm.querySelector(".arrival"),departureDate = searchForm.querySelector(".departure");searchButton.addEventListener("click", function (a) {a.preventDefault(),searchForm.classList.toggle("search-body-show"),searchForm.classList.remove("form-error")}),searchForm.addEventListener("submit", function (a) {if (!arrivalDate.value&&!departureDate.value) {a.preventDefault(),searchForm.classList.remove("form-error"),searchForm.offsetWidth = searchForm.offsetWidth,searchForm.classList.add("form-error")}}),window.addEventListener("keydown", function (a) {if (a.keyCode === 27) {if (searchForm.classList.contains("search-body-show")) {a.preventDefault(),searchForm.classList.remove("search-body-show"),searchForm.classList.remove("form-error")}}});